// ----------------------------------------------------------------------------// [b12] Java Source File: DropPanel.java//                created: 09.01.2004//              $Revision: 1.1 $// ----------------------------------------------------------------------------package b12.panik.ui;import java.awt.Dimension;import java.awt.GridLayout;import java.awt.datatransfer.DataFlavor;import java.awt.datatransfer.Transferable;import java.awt.datatransfer.UnsupportedFlavorException;import java.awt.dnd.*;import java.awt.dnd.DropTarget;import java.awt.dnd.DropTargetListener;import java.io.IOException;import javax.media.Track;import javax.swing.*;import javax.swing.JLabel;import javax.swing.JPanel;import javax.swing.TransferHandler;class DropPanel extends JPanel {        DropTarget dropTarget;    	public DropPanel() {	    super(new GridLayout(0,1));	    final Dimension dimDrop = new Dimension(300, 200);	    setPreferredSize(dimDrop);	    setOpaque(false); //in order to see border of panel	    setTransferHandler(new TrackLabelTransferHandler());	    setDropTarget(new DropTarget());	}		void setPixelsPerSecond(int pps) {	    TrackLabel.setPixelsPerSecond(pps);	    repaint();	}		void addTrack(Track t) {	    addTrackLabel(new TrackLabel(t));	}		void addTrackLabel(TrackLabel label) {		SinglePanel small = new SinglePanel(label);		// add panel, one per row		add(small);	}		class SinglePanel extends JPanel {	    private JLabel label;        SinglePanel(JLabel label) {	        super(null);	        this.label = label;	        add(this.label);	    }	}}