// ----------------------------------------------------------------------------// [b12] Java Source File: TracksPanel.java//                created: 29.10.2003//              $Revision: 1.7 $// ----------------------------------------------------------------------------package b12.panik.ui;import java.awt.*;import java.awt.dnd.DnDConstants;import java.util.ArrayList;import java.util.Collection;import java.util.List;import javax.media.Track;import javax.swing.JPanel;import b12.panik.ui.util.RoundBorder;/** * The <code>TracksPanel</code> offers a visual control to display and alter * the tracks for the {@linkplain PanicPlayer}. */public class TracksPanel extends JPanel {    Collection tracks;    /**	 * Creates a new instance of <code>TracksPanel</code>.	 */    public TracksPanel() {        setLayout(new GridBagLayout());        setBorder(new RoundBorder("Drop Me", new Insets(1, 1, 1, 1)));        setBackground(Color.LIGHT_GRAY);        // drop panel        DropPanel dropPanel = new DropPanel();        // drag panel        DragPanel dragPanel = new DragPanel();        add(            dropPanel,            new GridBagConstraints(                0,                0,                1,                1,                1,                .6,                GridBagConstraints.CENTER,                GridBagConstraints.BOTH,                new Insets(0, 0, 0, 0),                0,                0));        add(            dragPanel,            new GridBagConstraints(                0,                1,                1,                1,                1,                .4,                GridBagConstraints.CENTER,                GridBagConstraints.BOTH,                new Insets(0, 0, 0, 0),                0,                0));        DragObject dragLabel = new DragObject("DragME", DnDConstants.ACTION_COPY_OR_MOVE);        dragLabel.setBackground(Color.white);        dragLabel.setOpaque(true);        dragPanel.add(dragLabel);        // TODO initialize with default information    }    /** @see javax.swing.JComponent#paintComponent(java.awt.Graphics) */    protected void paintComponent(Graphics g) {        if (ui != null) {            Graphics scratch = g.create();            try {                scratch.setColor(getBackground());                ((RoundBorder) getBorder()).paintInterior(scratch, 0, 0, getWidth(), getHeight());            } finally {                scratch.dispose();            }        }    }    /**	 * Sets the tracks for this <code>TracksPanel</code>.	 * 	 * @param tracks	 *            the new tracks.	 */    public void setTracks(Collection tracks) {        this.tracks = new ArrayList(tracks);    }    class DragPanel extends JPanel {        JPanel componentPanel;                public DragPanel() {            super(new GridBagLayout());            GridBagConstraints gbc = new GridBagConstraints();            gbc.gridx = 0;            gbc.gridx = 0;            gbc.anchor = GridBagConstraints.CENTER;            gbc.fill = GridBagConstraints.NONE;                        Dimension dimAvailable = new Dimension(300, 100);            setPreferredSize(dimAvailable);            setBorder(new RoundBorder("Drag Me"));            setOpaque(false); //in order to see border of panel            componentPanel = new JPanel();            add(componentPanel, gbc);        }                /** @see java.awt.Container#add(java.awt.Component) */        public Component add(Component comp) {            return componentPanel.add(comp);        }    }}