// ----------------------------------------------------------------------------// [b12] Java Source File: FlexibleLineBorder.java//                created: 10.01.2004//              $Revision: 1.1 $// ----------------------------------------------------------------------------package b12.panik.ui.util;import java.awt.Color;import javax.swing.border.LineBorder;/** * LineBorder with changeable color. * @author kariem */public class FlexibleLineBorder extends LineBorder {    /**     * Creates a new instance of <code>FlexibleLineBorder</code>.     * @param c the color to be used.     */    public FlexibleLineBorder(Color c) {        super(c);    }    /**     * Sets the color of this border.     * @param c the border color.     */    public void setColor(Color c) {        lineColor = c;    }}